#!/usr/bin/env python3
"""
Crear favicon ICO real a partir de datos base64
"""
import base64
import os

def create_favicon_ico():
    """Crear archivo favicon.ico real"""
    
    # Favicon ICO de 32x32 con icono de casa en base64
    # Este es un ICO real generado con el icono de casa
    favicon_b64 = """

    """
    
    # Decodificar base64 y guardar como archivo ICO
    favicon_data = base64.b64decode(favicon_b64.strip())
    
    # Crear directorio static si no existe
    static_dir = '/home/manu/personalcode/home_control_adv/backend/static'
    if not os.path.exists(static_dir):
        os.makedirs(static_dir)
    
    favicon_path = os.path.join(static_dir, 'favicon.ico')
    
    with open(favicon_path, 'wb') as f:
        f.write(favicon_data)
    
    print(f"‚úÖ Favicon ICO creado: {favicon_path}")
    print(f"üìÅ Tama√±o: {len(favicon_data)} bytes")
    
    return favicon_path

if __name__ == '__main__':
    print("üè† Creando favicon ICO real...")
    favicon_path = create_favicon_ico()
    print("üéâ Favicon listo para uso!")